"use client";

import { Slot } from "@radix-ui/react-slot";
import { cva, type VariantProps } from "class-variance-authority";
import React from "react";
import { cn } from "@/utils/cn";
import { Spinner } from "./spinner";
import { motion } from "framer-motion";
import { useRuru } from "@/provider";

export const buttonVariants = cva(
  "inline-flex items-center justify-center whitespace-nowrap rounded-md text-sm font-medium transition-colors focus-visible:outline-none focus-visible:ring-1 focus-visible:ring-ring disabled:pointer-events-none disabled:opacity-50",
  {
    variants: {
      variant: {
        default:
          "bg-primary text-primary-foreground shadow hover:bg-primary/85 hover:shadow-md",
        secondary:
          "border-input border-[1.5px] bg-primary-foreground hover:bg-[#f3f3f3] dark:hover:bg-[#202020]",
        tertiary: "text-primary hover:bg-[#f3f3f3] dark:hover:bg-[#202020]",
        error: "bg-[#d93036] hover:bg-[#ff6166]",
        warning: "bg-[#ff990a] text-primary-foreground hover:bg-[#d27504]",
      },
      size: {
        default: "h-9 px-4 py-2",
        small: "h-8 rounded-md px-3 text-xs",
        large: "h-10 rounded-md px-8",
        icon: "size-9",
      },
    },
    defaultVariants: {
      variant: "default",
      size: "default",
    },
  },
);

export interface ButtonProps
  extends Omit<
      React.ButtonHTMLAttributes<HTMLButtonElement>,
      "prefix" | "suffix"
    >,
    VariantProps<typeof buttonVariants> {

  asChild?: boolean;

  prefix?: React.ReactNode;

  suffix?: React.ReactNode;

  disabled?: boolean;

  loading?: boolean;
}

export const Button = React.forwardRef<HTMLButtonElement, ButtonProps>(
  (
    {
      className,
      variant = "default",
      size = "default",
      asChild = false,
      prefix,
      suffix,
      disabled = false,
      loading = false,
      ...props
    },
    ref,
  ) => {
    const { animation } = useRuru();

    const Comp = asChild ? Slot : "button";

    const buttonContent = (
      <Comp
        className={cn(
          buttonVariants({
            variant: loading ? "secondary" : variant,
            size,
          }),
          className,
        )}
        ref={ref}
        disabled={disabled}
        {...props}
      >
        {loading ? <Spinner className="mr-2" /> : null}
        {prefix ? (
          <span className="mr-2 flex items-center justify-center">
            {prefix}
          </span>
        ) : null}
        {props.children}
        {suffix ? (
          <span className="ml-2 flex items-center justify-center">
            {suffix}
          </span>
        ) : null}
      </Comp>
    );

    return (
      <div className={cn(className, disabled && "cursor-not-allowed ")}>
        {animation ? (
          <motion.div whileTap={{ scale: 0.93 }}>{buttonContent}</motion.div>
        ) : (
          buttonContent
        )}
      </div>
    );
  },
);

Button.displayName = "Button";
